const raw = $json["text"];

// Remove markdown formatting and excessive whitespace
let clean = raw
  .replace(/^\s*```json\s*/i, "")   // remove opening ```json
  .replace(/\s*```$/, "")           // remove closing ```
  .trim();

// Fix dangerous control characters that crash JSON.parse
clean = clean.replace(/[\u0000-\u001F]+/g, ''); // removes bad control chars like \u000A etc.

try {
  const parsed = JSON.parse(clean);
  return parsed;
} catch (err) {
  return {
    error: "‚ùå Failed to parse JSON",
    message: err.message,
    raw: clean.slice(0, 1000) // return preview of the cleaned JSON
  };
}
